def berlekamp_massey(bits):
    """
    bits: list of 0/1 (ints), e.g. [1,0,1,1,...]
    returns: connection polynomial C, linear complexity L
    """
    n = len(bits)
    C = [1]  # connection poly
    B = [1]
    L, m = 0, -1

    for N in range(n):
        # compute discrepancy
        d = bits[N]
        for i in range(1, L+1):
            if i < len(C):
                d ^= C[i] & bits[N - i]
        if d == 1:
            T = C[:]
            shift = [0]*(N - m) + B
            if len(C) < len(shift):
                C += [0]*(len(shift)-len(C))
            # xor
            C = [(ci ^ si) for ci, si in zip(C, shift)]
            if 2*L <= N:
                L = N + 1 - L
                B = T
                m = N
    return C, L


def lfsr_predict(seq, C, L, nbits):
    """
    Given observed seq (list of 0/1), connection poly C, and L,
    predict next nbits.
    """
    state = seq[-L:]  # last L bits
    out = []
    for _ in range(nbits):
        # recurrence: new = sum_{i=1..L} C[i]*state[-i]
        new = 0
        for i in range(1, L+1):
            if i < len(C):
                new ^= C[i] & state[-i]
        out.append(new)
        state.append(new)
    return out


